import React, { Component } from "react"; 
import Web3 from "web3";
import detectEthereumProvider from "@metamask/detect-provider";
//import KryptoBird from '../abis/Kryptobird.json'; 


class App extends Component {

    async componentDidMount() {
        await this.loadWeb3();
        await this.loadBlockchainData();
    }
    // first up is to detect ethereum provider (metamask)
    /*
    async loadWeb3(){
        const provider = await detectEthereumProvider();

        // modern browers 
        // if there is a provider then
        // lets log that it's working and access the window from the doc
        // to set Web3 to the provider 
        if(provider) {
            console.log('ethereum wallet is connected')

            const web3 = new Web3(provider)
            const accounts = await provider.request({ method: 'eth_accounts' });
            console.log(accounts)
        } else {
            // no ethereum provider 
            console.log('no ehtereum wallet detected')
        }
    } */

    async loadWeb3(){
        const provider = await detectEthereumProvider();

        if(provider) {
            console.log('ethereum wallet is connected')
            // From now on, this should always be true:
            // provider === window.ethereum

            // Legacy providers may only have ethereum.sendAsync
            const chainId = await provider.request({
                method: 'eth_chainId'
            })
        } else {
            // no ethereum provider 
            console.log('no ehtereum wallet detected')
        }
    }

    async loadBlockchainData() {
        const accounts = await window.ethereum.getAccounts();
        console.log(accounts);
        console.log(accounts[0]);
    }

    render(){
        return (
            <div>
                <h1>NFT Marketplace</h1>
            </div>
        )
    }
}

export default App;
